"$:/plugins/dyp/Sidenote & Sidelink/styles/css": {
    "created": "20240131061341204",
    "text": ".yin {
  display: inline;
  opacity: 0;
  transition: opacity 0.5s ease-in-out; /* 过渡效果应用于不透明度，持续时间为0.5秒，缓动函数为ease-in-out */
  font-size: 0.5em;
  color: #1A4975;
  vertical-align: super;
}

.yin:hover {
  opacity: 1;
}

.break
{
word-break: normal; 
word-wrap: break-word;
white-space: pre-wrap;
}

.sidenote {
  float: right;
  clear: right;
  max-width: 300px; /* 设置最大宽度为300像素 */
  margin-top: 0.3rem;
  margin-bottom: 0;
  line-height: 1.3;
  vertical-align: baseline;
  position: relative;
  border: 1px solid #ddd; /* 添加边框 */
  border-radius: 8px; /* 边框圆角 */
  padding: 10px; /* 增加内边距 */
  color: hsl(206, 11%, 28%, 1); /* #1A4975 转换为 HSL 并减少饱和度 */
  background-color: hsl(194, 53%, 84%, 1); /* #B1D3E9 转换为 HSL 并减少饱和度 */
}

/* 定义大屏宽度 */
.large-screen {
  width: 1000px;
}

/* 定义小屏宽度 */
.small-screen {
  width: 350px;
}

/* 媒体查询，大屏 */
@media (min-width: 768px) {
.sidenote {
  margin-left: -95%; /* 条目外部 */
  width: 20%; 
  font-size: 0.9rem;
}
}

/* 媒体查询，小屏 */
@media (max-width: 767px) {
.sidenote {
  margin-left: 1%; /* 条目内部 */
  width: auto; 
  font-size: 0.9rem;
  max-width: 50%;
  bottom: 2em; /* 相对位置上移 */
  margin-bottom: -2em;
}
}

.sidenote summary
{
position: sticky;
top: 0;

  display: block; /* 或其他适当的显示方式 */
  cursor: pointer; /* 可以根据需要修改 */
  background: rgba(0, 0, 0, 0); /*设置背景色*/
  border: none; /* 去除边框 */
  -webkit-tap-highlight-color: transparent;
  box-shadow: none;
}

.sidenote blockquote {
  max-height: 200px; /* 设置最大高度为200px */
  overflow-y: auto; /* 内容超过最大高度时滚动 */
  border: none;
  margin-left: 0em;
  margin-top: -0.2em;
  margin-bottom: -0.2em;
  margin-right: -0.5em;
  padding: 0em;

  /* 下面是隐藏滚动条的样式 */
  scrollbar-width: none;  /* 对于Firefox浏览器 */
}

.sidenote blockquote::-webkit-scrollbar { /* 对于基于WebKit的浏览器，如Chrome、Safari等 */
  display: none; /* 隐藏滚动条 */
}

.sidenote-number {
  position: relative;
  top: -0.5em;
  right: 0;
  counter-increment: sidenote;
  padding-left: 20px;
  font-size: 0.7rem; /* 调整字体大小，以匹配上标样式 */
}

.sidenote-number::before {
  content: counter(sidenote);
  position: absolute;
  top: 0;
  left: 0;
  width: 12px;
  height: 12px;
  line-height: 12px;
  text-align: center;
  color: #1A4975;
  background-color: #B1D3E9; /* #f0f0f0; */
  border-radius: 50%;
  opacity: 0.8;
}

.sidenote summary::before {
  content: counter(sidenote) ". ";
  font-size: 0.9rem; /* 根据需要调整字体大小 */
  color: #333; /* 根据需要调整文字颜色 */
}",
    "title": "$:/plugins/dyp/Sidenote & Sidelink/styles/css",
